<?phpuse app\models\modelGame;if (! function_exists('db')) {    function db()    {        return new modelGame();    }}if (! function_exists('now')) {    function now()    {        return date('Y-m-d H:i:s');    }}if (! function_exists('request')) {    function request()    {        switch($_SERVER['REQUEST_METHOD'])        {            case 'GET': $the_request = &$_GET; break;            case 'POST': $the_request = &$_POST; break;            default:        }        return $the_request;    }}if (! function_exists('createSession')) {    function createSession()    {        session_start();        if (!empty($_SESSION['deleted_time']) && $_SESSION['deleted_time'] < time() - 180) {            session_destroy();            session_start();        }        //$_SESSION['destroyed'] = time();        //session_regenerate_id();        unset($_SESSION['destroyed']);        return $_SESSION['user'] = session_id();    }}if (! function_exists('regenerateSession')) {    function regenerateSession()    {        session_destroy();        session_start();        $_SESSION['destroyed'] = time();        session_regenerate_id();        unset($_SESSION['destroyed']);        return $_SESSION['user'] = session_id();    }}if (! function_exists('regenerateSessionForMaxRecords')) {    function regenerateSessionForMaxRecords($records)    {        if ($records && count($records) >= 9) {            session_destroy();            session_start();            $_SESSION['destroyed'] = time();            session_regenerate_id();            unset($_SESSION['destroyed']);            return $_SESSION['user'] = session_id();        }    }}if (! function_exists('sessionUser')) {    function sessionUser()    {        $has_session = session_status() == PHP_SESSION_ACTIVE ? true : false;        if ($has_session) {            return $_SESSION['user'];        } else {           print_r('no existe session de susuario');        }    }}if (! function_exists('dump')) {    function dump()    {        if (func_num_args() === 0) {            return;        }        $args = func_get_args();        $output = array();        foreach ($args as $arg) {            if (gettype($arg) == 'boolean') {                $value = $arg ? 'true' : 'false';            } else {                $value = print_r($arg, true);            }            $output[] = '<pre>('.gettype($arg).') '.htmlspecialchars($value).'</pre>';        }        echo implode("\n", $output);    }}if (! function_exists('dd')) {    function dd()    {        dump(...func_get_args());        exit();    }}